import React, {Component} from "react";

import Calendar from 'react-calendar';

import Tooltip from 'react-bootstrap/Tooltip';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Modal from 'react-bootstrap/Modal';

import './css/Calendar.css';
import './css/Events.css';

/*
class to encapsulate events, is passed between calendar and EventTicker
to populate Modals
*/
class Event {
  constructor(name, date, description){
    this.name = name;
    this.date = date;
    this.description = description;
  }
}

/*
component to be displayed on a calendar tile
*/
class EventTicker extends Component{
  constructor(props){
    super(props)

    this.state = {
      event_name:null,
      event_date:null,
      event_description:null,
      show_modal:false
    }

    this.handleOpen = this.handleOpen.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  handleOpen(event){
    this.setState({
      event_name:event.name,
      event_date:event.date,
      event_description:event.description,
      show_modal:true
    })
  }

  handleClose(){
    this.setState({
      event_name:null,
      event_date:null,
      event_description:null,
      show_modal:false
    })
  }

  render(){
    return(
      <div>
        <Modal className="nominate-modal" size="lg" show={this.state.show_modal} onHide={this.handleClose} centered>
          <Modal.Header className="text-24pt" closeButton>
            <Modal.Title>{this.state.event_name}</Modal.Title>
          </Modal.Header>
          <Modal.Body className="body-16pt">
            {this.state.event_description}
          </Modal.Body>
        </Modal>

        <OverlayTrigger
          placement="right"
          delay={{ show: 250, hide: 400 }}
          overlay={
                   <Tooltip id="button-tooltip">
                     Simple tooltip
                   </Tooltip>
                 }
        >
          <div className="dot" onClick={()=>this.handleOpen(this.props.event)}>
          </div>
        </OverlayTrigger>
      </div>
    )
  }
}

class EventsComponent extends Component{

  /*
  returns components corresponding to a given date, null otherwise
  */
  isEventDate(date){
    var s_e = new Event("MyEvent", "Date", "Description")

    if(date.getDay()===1){
      return [<br/>, <EventTicker event={s_e}/>]
    }
  }

  render(){
    return(
      <div>
        <p className="text-24pt page-title">
           Events
        </p>
        <div className="blue-yellow-bar"/>
        <Calendar
          style={{margin:"auto"}}
          tileContent={({ activeStartDate, date, view }) => this.isEventDate(date)}
          onDrillDown={({ activeStartDate, view }) => null}
          onDrillUp={({ activeStartDate, view })=>null}/>
      </div>
    )
  }
}

export default EventsComponent;
