import React, {Component} from "react"

import "./css/People.css";

import Carousel from 'react-bootstrap/Carousel';
import Modal from "react-bootstrap/Modal";

import member from './imgs/member.jpg'

class PeopleComponent extends Component{

  constructor(props){
    super(props)

    this.state = {show:false};

    this.handleClose = this.handleClose.bind(this);
    this.handleOpen = this.handleOpen.bind(this);
  }

  handleClose(){
    this.setState({show:false})
  }

  handleOpen(){
    this.setState({show:true})
  }

  render(){

    //item to hold the images that will be displayed in the carousel
    var carousel_items = [
      <Carousel.Item>
        <img
          className="d-block"
          src={member}
          width="462px"
          height="1004px"
        />
      </Carousel.Item>,
      <Carousel.Item>
        <img
          className="d-block"
          src={member}
          width="462px"
          height="1004px"
        />
      </Carousel.Item>,
      <Carousel.Item>
        <img
          className="d-block"
          src={member}
          width="462px"
          height="1004px"
        />
      </Carousel.Item>
    ];

    return(
      <div>

        <p className="text-24pt page-title">
           Member Monday
        </p>
        <div className="blue-yellow-bar"/>

        <div style={{display:"flex", flexWrap:"wrap", justifyContent:"center"}}>
          <Carousel style={{margin:"50px", width:"462px"}}>
            {carousel_items}
          </Carousel>

          <div style={{margin:"50px", textAlign:"center", width:"600px"}}>
            <p className="nominate-title">
              Member Monday Nomination
            </p>
            <p className="nominate-body">
              Every week a member of ACP is chosen to be recognized.
              Nominate one of your ACP friends to be the featured member
              this Monday on the Instagram!
            </p>
            <button className="my-button">
              Nominate!
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default PeopleComponent;
