import React, {Component} from "react"

import './css/Faces.css';

/*
a card that holds an embedded youtube video
guide to embedding youtube videos with html
https://www.w3schools.com/html/html_youtube.asp
*/
class VideoCard extends Component {
  constructor(props){
    super(props)
  }

  render(){
    return(
      <div className="video-card">
        <p className="video-card-title">
          {this.props.title}
        </p>
        <iframe className="video-card-video"
          src={this.props.link}>
        </iframe>
        <p className="video-card-caption">
          {this.props.caption}
        </p>
      </div>
    )
  }
}

class VideoCarousel extends Component{
  constructor(props){
    super(props);

    this.state = {
      index: 0,
      links: ["https://www.youtube.com/embed/gnPQYtEEIgE",
              "https://www.youtube.com/embed/bkFnekSAcxY"],
      titles: ["Faces of Austin - Antonelli's Cheese Shop",
               "Faces of Austin - Julio's Cafe"],
      captions: ["Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
                 "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."]
    };

    //bind this to methods
    this.decrement_index = this.decrement_index.bind(this);
    this.increment_index = this.increment_index.bind(this);
  }

  /*
  decrement index to display the previous video
  wraps around to the last video in the list
  */
  decrement_index(){
    var new_index = (this.state.index - 1 ) % this.state.links.length
    if(new_index < 0){
      new_index = this.state.links.length-1
    }
    this.setState({index:new_index})

  }

  /*
  increment index to display the next video
  wraps around to the first video in the list
  */
  increment_index(){
    this.setState({index:(this.state.index + 1 ) % this.state.links.length})
  }

  render(){
    return(
      <div style={{display: "grid",
                  gridTemplateColumns: "1fr 1fr 1fr",
                  alignItems:"center",
                  justifyContent:"center"
                }}>

        <div>
          <button
          className="carousel-button"
          style={{float:"right", margin:"15px"}}
          onClick={()=>{this.decrement_index()}}>
            &#60;
          </button>
        </div>

        <VideoCard
          title={this.state.titles[this.state.index]}
          link={this.state.links[this.state.index]}
          caption={this.state.captions[this.state.index]}
        />

        <div>
          <button
          className="carousel-button"
          style={{float:"left", margin:"15px"}}
          onClick={()=>{this.increment_index()}}>
            &#62;
          </button>
        </div>
      </div>
    )
  }
}

class FacesComponent extends Component{
  render(){
    return(
      <div>
        <p className="text-24pt page-title">
           Faces of Austin
        </p>
        <div className="blue-yellow-bar"/>
        <p className="text-16pt centered-text" style={{textAlign:"center", marginBottom:"50px"}}>
           Faces of Austin is a mini documentary series put on by ACP members
           that spotlights different local businesses and the people working
           there around Austin. Check out some of our latest features!
        </p>

        <VideoCarousel />
      </div>
    )
  }
}

export default FacesComponent;
